INSERT INTO `course` VALUES (3618, 'CPSC', 203, 'Introduction to Problem Solving using Application Software', 'Introduction to computer fundamentals; contemporary topics, such as security and privacy, and the Internet and World Wide Web. Problem solving, analysis and design using application software such as spreadsheets and word processors.', null, 3.0, null, 'Not open for registration to Computer Science majors.', null, false, false, null);
INSERT INTO `course` VALUES (3619, 'CPSC', 217, 'Introduction to Computer Science for Multidisciplinary Studies I', 'Introduction to problem solving, analysis and design of small-scale computational systems and implementation using a procedural programming language. For students wishing to combine studies in computer science with studies in other disciplines.', null, 3.0, null, 'Credit for <course cid="3619">Computer Science 217</course> and any of 215, <course cid="3621">231</course>, <course cid="3623">235</course>, <course cid="43036">Data Science 211</course>, Computer Engineering 339, Engineering 233 or <course cid="47158">Digital Engineering 233</course> will not be allowed.', null, false, false, 'See the statements at the beginning of the Computer Science entry.');
INSERT INTO `course` VALUES (3620, 'CPSC', 219, 'Introduction to Computer Science for Multidisciplinary Studies II', 'Continuation of Introduction to Computer Science for Multidisciplinary Studies I. Emphasis on object oriented analysis and design of small-scale computational systems and implementation using an object oriented language. Issues of design, modularization and programming style will be emphasized.', null, 3.0, '<course cid="3619">Computer Science 217</course> or <course cid="43036">Data Science 211</course>.', 'Credit for <course cid="3620">Computer Science 219</course> and any of <course cid="3622">233</course>, <course cid="3623">235</course>, Electrical Engineering 497 or Computer Engineering 493 will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3621, 'CPSC', 231, 'Introduction to Computer Science for Computer Science Majors I', 'Introduction to problem solving, the analysis and design of small-scale computational systems, and implementation using a procedural programming language. For computer science majors.', null, 3.0, null, 'Credit for <course cid="3621">Computer Science 231</course> and any of Computer Science 215, <course cid="3619">217</course>, <course cid="3623">235</course>, <course cid="43036">Data Science 211</course>, Computer Engineering 339, Engineering 233, or <course cid="47158">Digital Engineering 233</course> will not be allowed.', null, false, false, 'See the statements at the beginning of the Computer Science entry.');
INSERT INTO `course` VALUES (3622, 'CPSC', 233, 'Introduction to Computer Science for Computer Science Majors II', 'Continuation of Introduction to Computer Science for Computer Science Majors I. Emphasis on object-oriented analysis and design of small-scale computational systems and implementation using an object oriented language. Issues of design, modularization, and programming style will be emphasized.', null, 3.0, '<course cid="3621">Computer Science 231</course>.', 'Credit for <course cid="3622">Computer Science 233</course> and any of <course cid="3620">219</course>, <course cid="3623">235</course>, Electrical Engineering 497 or Computer Engineering 493 will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3623, 'CPSC', 235, 'Advanced Introduction to Computer Science', 'An accelerated introduction to problem solving, the analysis and design of small-scale computational systems and implementation using both procedural and object oriented programming languages. Issues of design, modularization, and programming style will be emphasized.', null, 3.0, 'Consent of the Department.', 'Credit for <course cid="3623">Computer Science 235</course> and any of 215, <course cid="3619">217</course>, <course cid="3620">219</course>, <course cid="3621">231</course>, <course cid="3622">233</course>, Computer Engineering 339, 493 or <course cid="43036">Data Science 211</course> will not be allowed.', null, false, false, 'See the statements at the beginning of the Computer Science entry.');
INSERT INTO `course` VALUES (44642, 'CPSC', 251, 'Theoretical Foundations of Computer Science I', 'Proof techniques. Recursion and iteration. Specification of algorithmic problems, proofs of correctness, and bounds on their computational complexity. Discrete structures such as graphs, trees, strings, functions, and their computer representation. Counting, permutations, discrete probability spaces, random events. Applications in Computer Science.', null, 3.0, '<course cid="3620">Computer Science 219</course> or <course cid="3621">231</course>.', 'Students may not register in, or have credit for, <course cid="44642">Computer Science 251</course> if they have previous credit for one of <course cid="6947">Mathematics 271</course> or <course cid="6948">273</course> or are concurrently enrolled in <course cid="6947">Mathematics 271</course> or <course cid="6948">273</course>.', null, false, false, null);
INSERT INTO `course` VALUES (47031, 'CPSC', 304, 'A Survey of Computer Science for Non-Majors', 'An accessible exploration of different computing topics. Emphasizing a familiarity with a broad array of subjects within computer science rather than depth. Topics may include computing hardware, data representation, data compression, computer graphics, sorting algorithms, artificial intelligence techniques, databases, networks and security and privacy.', null, 3.0, null, 'Not open for registration to Majors in Computer Science, Bioinformatics, Electrical Engineering, Software Engineering, Computer Engineering, Natural Sciences with a primary concentration in Computer Science or any student with at least 3 units of courses numbered 300 or above and labelled Computer Science, Software Engineering or Software Engineering for Engineers.', null, false, false, null);
INSERT INTO `course` VALUES (3626, 'CPSC', 313, 'Introduction to Computability', 'An introduction to abstract models of sequential computation, including finite automata, regular expressions, context-free grammars, and Turing machines. Formal languages, including regular, context-free, and recursive languages, methods for classifying languages according to these types, and relationships among these classes.', null, 3.0, '<course cid="6947">Mathematics 271</course> or <course cid="6948">273</course>; and <course cid="10449">Philosophy 279</course> or <course cid="10469">377</course>; and 3 units from <course cid="3620">Computer Science 219</course>, <course cid="3622">233</course> or <course cid="3623">235</course>.', 'Credit for <course cid="3626">Computer Science 313</course> and <course cid="44677">Computer Science 351</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (30258, 'CPSC', 319, 'Data Structures, Algorithms, and Their Applications', 'Fundamental data structures, including arrays, lists, stacks, queues, trees, hash tables, and graphs. Algorithms for searching and sorting. Introduction to analysis of algorithms. Applications of these data structures and algorithms. For students wishing to combine studies in computer science with studies in other disciplines.', null, 3.0, '3 units from <course cid="3620">Computer Science 219</course>, <course cid="3622">233</course>, <course cid="3623">235</course>, <course cid="43065">Computer Engineering 335</course>, 339 or <course cid="43066">Software Engineering for Engineers 337</course>.', 'Credit for <course cid="30258">Computer Science 319</course> and <course cid="3628">331</course> will not be allowed. Computer Science majors are not permitted to register in this course.', null, false, false, null);
INSERT INTO `course` VALUES (30259, 'CPSC', 329, 'Explorations in Information Security and Privacy', 'A broad survey of topics in information security and privacy, with the purpose of cultivating an appropriate mindset for approaching security and privacy issues. Topics will be motivated by recreational puzzles. Legal and ethical considerations will be introduced as necessary.', null, 3.0, 'Mathematics 30-1, 30-2 or 31.', null, null, false, false, null);
INSERT INTO `course` VALUES (3628, 'CPSC', 331, 'Data Structures, Algorithms, and Their Analysis', 'Fundamental data structures, including arrays, lists, stacks, queues, trees, hash tables, and graphs. Algorithms for searching and sorting. Introduction to the correctness and analysis of algorithms. For computer science majors and those interested in algorithm design and analysis, information security, and other mathematically-intensive areas.', null, 3.0, '3 units from <course cid="44642">Computer Science 251</course>, <course cid="6947">Mathematics 271</course> or <course cid="6948">273</course>; and 3 units from <course cid="3620">Computer Science 219</course>, <course cid="3622">233</course>, <course cid="3623">235</course>, <course cid="43065">Computer Engineering 335</course>, 339 or <course cid="43066">Software Engineering for Engineers 337</course>.', 'Credit for <course cid="3628">Computer Science 331</course> and <course cid="30258">319</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3629, 'CPSC', 335, 'Intermediate Information Structures', 'A continuation of Computer Science 319 or 331 . Collision resolution in hash tables, search algorithms, advanced tree structures, strings. Advanced algorithmic tools for the storing and manipulation of information.', null, 3.0, '<course cid="30258">Computer Science 319</course> or <course cid="3628">331</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (44677, 'CPSC', 351, 'Theoretical Foundations of Computer Science II', 'Probability spaces, random variables, expectation, independence, concentration bounds, conditional probability, repeated trials, error bounds. Turing machines and finite automata. Deterministic, nondeterministic and probabilistic models of computation. Languages, regular languages and regular expressions. Methods for classifying computational problems as decidable or undecidable.', null, 3.0, '3 units from <course cid="3620">Computer Science 219</course>, <course cid="3622">233</course> or <course cid="3623">235</course>; and <course cid="44642">Computer Science 251</course> or with department consent <course cid="6337">Statistics 213</course> and either <course cid="6947">Mathematics 271</course> or <course cid="6948">273</course>; and 3 units from <course cid="6944">Mathematics 249</course>, <course cid="40094">265</course> or <course cid="40096">275</course>; and <course cid="10449">Philosophy 279</course> or <course cid="10469">377</course>.', 'Credit for <course cid="44677">Computer Science 351</course> and <course cid="3626">Computer Science 313</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3631, 'CPSC', 355, 'Computing Machinery I', 'An introduction to computing machinery establishing the connection between programs expressed in a compiled language, an assembly language, and machine code, and how such code is executed. Includes the detailed study of a modern CPU architecture, its assembly language and internal data representation, and the relationship between high-level program constructs and machine operations.', null, 3.0, '3 units from <course cid="3620">Computer Science 219</course>, <course cid="3622">233</course> or <course cid="3623">235</course>.', 'Credit for <course cid="3631">Computer Science 355</course> and 265 or <course cid="7588">Computer Engineering 369</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (38542, 'CPSC', 359, 'Computing Machinery II', 'An introduction to hardware and microprocessor design, including the connection between gate-level digital logic circuits and sequential machines that can execute an algorithm and perform input and output.', null, 3.0, '<course cid="3631">Computer Science 355</course> and <course cid="10449">Philosophy 279</course> or <course cid="10469">377</course>.', 'Credit for <course cid="38542">Computer Science 359</course> and any of 325, 455 or Computer Engineering 415 will not be allowed.', null, false, false, 'A supplementary fee will be assessed to cover additional costs associated with this course.');
INSERT INTO `course` VALUES (40995, 'CPSC', 399, 'Special Topics in Computer Science', 'Exploration of various areas in Computer Science. Topics will vary from year-to-year. It will be offered as required to provide the opportunity for students to engage in additional areas in Computer Science. Before registration, consult the Department of Computer Science for topics offered.', null, 3.0, 'Consent of the Department.', null, null, false, true, null);
INSERT INTO `course` VALUES (43029, 'CPSC', 405, 'Software Entrepreneurship', 'Development of business models, building software prototypes and creation of pitch presentations to create a software-based business.', null, 3.0, '<course cid="42224">Software Engineering 300</course> or 301.', null, null, false, false, 'Taught in collaboration with the Haskayne School of Business.');
INSERT INTO `course` VALUES (3633, 'CPSC', 409, 'History of Computation', 'The history of computation from the earliest times to the modern era.', null, 3.0, '<course cid="3631">Computer Science 355</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3634, 'CPSC', 411, 'Compiler Construction', 'Introduction to compilers, interpreters, and the tools for parsing and translation. Lexical analysis, context free grammars and software tools for their recognition. Attribute grammars and their applications in translation and compiling.', null, 3.0, '<course cid="30258">Computer Science 319</course> or <course cid="3628">331</course>; and <course cid="3631">Computer Science 355</course> or <course cid="7588">Computer Engineering 369</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3635, 'CPSC', 413, 'Design and Analysis of Algorithms I', 'Techniques for the analysis of algorithms, including counting, summation, recurrences, and asymptotic relations; techniques for the design of efficient algorithms, including greedy methods, divide and conquer, and dynamic programming; examples of their application; an introduction to tractable and intractable problems.', null, 3.0, '<course cid="3628">Computer Science 331</course>; and <course cid="3626">313</course> or <course cid="44677">351</course>; and <course cid="6942">Mathematics 211</course> or <course cid="6943">213</course>; and 3 units from <course cid="6944">Mathematics 249</course>, <course cid="40094">265</course> or <course cid="40096">275</course>.', null, null, false, false, 'Students who have credit for <course cid="30258">Computer Science 319</course> instead of <course cid="3628">Computer Science 331</course> should contact the department for instructions on how to enrol in this course.');
INSERT INTO `course` VALUES (30260, 'CPSC', 418, 'Introduction to Cryptography', 'The basics of cryptography, with emphasis on attaining well-defined and practical notations of security. Symmetric and public key cryptosystems; one-way and trapdoor functions; mechanisms for data integrity; digital signatures; key management; applications to the design of cryptographic systems. In addition to written homework, assessment will involve application programming; additional mathematical theory and proof-oriented exercises will be available for extra credit.', null, 3.0, '<course cid="3628">Computer Science 331</course> and 3 units from <course cid="44677">Computer Science 351</course>, <course cid="6947">Mathematics 271</course>, <course cid="6948">273</course>, <course cid="43123">315</course> or Pure Mathematics 315.', 'Credit for <course cid="30260">Computer Science 418</course> and any of Computer Science 429, 557, <course cid="43124">Mathematics 318</course>, Pure Mathematics 329 or 418 will not be allowed.', null, false, false, 'Students who have credit for <course cid="30258">Computer Science 319</course> instead of <course cid="3628">Computer Science 331</course> should contact the department for instructions on how to enrol in this course.');
INSERT INTO `course` VALUES (3638, 'CPSC', 433, 'Artificial Intelligence', 'An examination of the objectives, key techniques and achievements of work on artificial intelligence in Computer Science.', null, 3.0, '<course cid="3626">Computer Science 313</course> or <course cid="44677">351</course>; and <course cid="10449">Philosophy 279</course> or <course cid="10469">377</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3639, 'CPSC', 441, 'Computer Networks', 'Principles and practice in modern telecommunications, computer communications and networks. Layered communication protocols and current physical, data link, network and Internet protocol layers. Circuit switching, packet switching, and an introduction to broadband multimedia networking.', null, 3.0, '<course cid="30258">Computer Science 319</course> or <course cid="3628">331</course>; and <course cid="3631">Computer Science 355</course> or <course cid="7588">Computer Engineering 369</course>.', 'Credit for <course cid="3639">Computer Science 441</course> and <course cid="7639">Electrical Engineering 573</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3640, 'CPSC', 449, 'Programming Paradigms', 'Examination of the basic principles of the major programming language paradigms. Focus on declarative paradigms such as functional and logic programming. Data types, control expressions, loops, types of references, lazy evaluation, different interpretation principles, information hiding.', null, 3.0, '<course cid="30258">Computer Science 319</course> or <course cid="3628">331</course>; and <course cid="10449">Philosophy 279</course> or <course cid="10469">377</course>.', 'Credit for <course cid="3640">Computer Science 449</course> and 349 will not be allowed.', null, false, false, 'The prerequisite of <course cid="10449">Philosophy 279</course> or <course cid="10469">377</course> is waived for Engineering students in the Software Engineering program.');
INSERT INTO `course` VALUES (3641, 'CPSC', 453, 'Introduction to Computer Graphics', 'Introduction to computer graphics. Principles of raster image generation. Example of a graphics API. Graphics primitives. Co-ordinate systems, affine transformations and viewing of graphical objects. Introduction to rendering including shading models and ray tracing. Introduction to modelling including polygon meshes, subdivision, and parametric curves and surfaces.', null, 3.0, '<course cid="30258">Computer Science 319</course> or <course cid="3628">331</course>; and <course cid="6942">Mathematics 211</course> or <course cid="6943">213</course>; and 3 units from Mathematics 253, <course cid="40095">267</course>, <course cid="40097">277</course>, 283 or Applied Mathematics 219.', null, null, false, false, null);
INSERT INTO `course` VALUES (3642, 'CPSC', 457, 'Principles of Operating Systems', 'An introduction to operating systems principles. Performance measurement; concurrent programs; the management of information, memory and processor resources.', null, 3.0, '<course cid="30258">Computer Science 319</course> or <course cid="3628">331</course>; and <course cid="3631">Computer Science 355</course> or <course cid="7588">Computer Engineering 369</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3643, 'CPSC', 461, 'Information Structures', 'File architecture and manipulation techniques for various file types. Physical characteristics of current mass storage devices. Advanced data structures and algorithms for implementing various sequential and hierarchical file structures. File organization and design for various applications, file systems and other storage management techniques including website design.', null, 3.0, '<course cid="3631">Computer Science 355</course>; and <course cid="30258">319</course> or <course cid="3628">331</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3644, 'CPSC', 471, 'Data Base Management Systems', 'Conceptual, internal and external data bases. Relational data base systems and SQL. The normal forms, data base design, and the entity-relationship approach.', null, 3.0, '<course cid="30258">Computer Science 319</course> or <course cid="3628">331</course>.', 'Credit for <course cid="3644">Computer Science 471</course> and <course cid="41132">Business Technology Management 331</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3645, 'CPSC', 481, 'Human-Computer Interaction I', 'Fundamental theory and practice of the design, implementation, and evaluation of human-computer interfaces. Topics include: principles of design; methods for evaluating interfaces with or without user involvement; techniques for prototyping and implementing graphical user interfaces.', null, 3.0, '3 units from <course cid="42224">Software Engineering 300</course>, 301, Software Engineering 480 or <course cid="43039">Data Science 311</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3646, 'CPSC', 491, 'Techniques for Numerical Computation', 'Elementary techniques for the numerical solution of mathematical problems on a computer, including methods for solving linear and non-linear equations, numerical integration, and interpolation.', null, 3.0, '<course cid="30258">Computer Science 319</course> or <course cid="3628">331</course>; and <course cid="6942">Mathematics 211</course> or <course cid="6943">213</course>; and 3 units from <course cid="6944">Mathematics 249</course>, <course cid="40094">265</course> or <course cid="40096">275</course>.', 'Credit for <course cid="3646">Computer Science 491</course> and any of <course cid="43133">Mathematics 391</course>, <course cid="43140">493</course> or Engineering 407 or <course cid="40921">Chemical Engineering 407</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (40996, 'CPSC', 499, 'Special Topics in Computer Science', 'Exploration of various areas in Computer Science. Topics will vary from year-to-year. It will be offered as required to provide the opportunity for students to engage in additional areas in Computer Science. Before registration, consult the Department of Computer Science for topics offered.', null, 3.0, 'Consent of the Department.', null, null, false, true, null);
INSERT INTO `course` VALUES (3647, 'CPSC', 501, 'Advanced Programming Techniques', 'Theory and application of advanced programming methods and tools. Recent issues as well as those of an enduring nature will be discussed.', null, 3.0, 'Computer Science 349 or <course cid="3640">449</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3648, 'CPSC', 502, 'Research Project', 'A substantial research project under the guidance of a faculty member. A report must be written and presented on completion of the course.', null, 6.0, 'Consent of the Department.', 'Credit for <course cid="3648">Computer Science 502</course> and any of <course cid="3649">503</course>, Software Engineering for Engineers 599 or 591 will not be allowed.', null, false, false, 'Students intending to complete a research project in a specific area of computer science should register in the version of <course cid="3648">Computer Science 502</course> corresponding to that area, if such a course is available. Other students should register in <course cid="3648">Computer Science 502</course>.01. Permission to register in <course cid="3648">Computer Science 502</course> is generally given only to students with a minimum GPA of 3.30 over the last 90 units. When demand exceeds capacity, registration in <course cid="3648">Computer Science 502</course> is limited to students in Honours programs in Computer Science.');
INSERT INTO `course` VALUES (3649, 'CPSC', 503, 'Project', 'A research project conducted under the guidance of a faculty member. A report must be presented on completion of the course.', null, 3.0, 'Consent of the Department.', 'Credit for <course cid="3649">Computer Science 503</course> and either <course cid="3648">502</course> or Software Engineering for Engineers 599 will not be allowed.', null, false, false, 'Students intending to complete a project in a specific area of computer science should register in the version of <course cid="3649">Computer Science 503</course> corresponding to that area, if such a course is available. Other students should register in <course cid="3649">Computer Science 503</course>.01.');
INSERT INTO `course` VALUES (3651, 'CPSC', 511, 'Introduction to Complexity Theory', 'Time and space complexity; the classes P, LOGSPACE, PSPACE and their nondeterministic counterparts; containments and separations between complexity classes; intractability and the theory of NP-completeness; complexity theories for probabilistic algorithms and for parallel algorithms.', null, 3.0, '<course cid="3635">Computer Science 413</course>.', 'Credit for <course cid="3651">Computer Science 511</course> and <course cid="3683">611</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3652, 'CPSC', 513, 'Computability', 'Computable functions; decidable and undecidable problems; Church's thesis and recursive functions.', null, 3.0, '<course cid="3626">Computer Science 313</course> or <course cid="44677">351</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3653, 'CPSC', 517, 'Design and Analysis of Algorithms II', 'Advanced techniques for the design and analysis of deterministic and probabilistic algorithms; techniques for deriving lower bounds on the complexity of problems.', null, 3.0, '<course cid="3635">Computer Science 413</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3654, 'CPSC', 518, 'Introduction to Computer Algebra', 'Fundamental problems, classical and modern algorithms, and algorithm design and analysis techniques of use in computer algebra. Integer and polynomial arithmetic. Additional problems in computer algebra, possibly including problems in computational linear algebra, factorization, and concerning systems of polynomial equations will be considered as time permits.', null, 3.0, '<course cid="3635">Computer Science 413</course>; and <course cid="6942">Mathematics 211</course> or <course cid="6943">213</course>.', 'Credit for <course cid="3654">Computer Science 518</course> and <course cid="3695">Computer Science 667</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3655, 'CPSC', 519, 'Introduction to Quantum Computation', 'Introduction to quantum computing. Quantum algorithms, quantum search, quantum fourier transforms, quantum error correcting codes, quantum cryptography, nonlocality and quantum communication complexity, and quantum computational complexity.', null, 3.0, '<course cid="3635">Computer Science 413</course>; and <course cid="6951">Mathematics 311</course> or <course cid="6952">313</course>.', 'Credit for <course cid="3655">Computer Science 519</course> and <course cid="3686">619</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3656, 'CPSC', 521, 'Foundations of Functional Programming', 'Theoretical foundations of functional programming: the lambda-calculus, beta-reduction, confluence, and reduction strategies. Programming syntax: solving recursive equations with the Y-combinator, let and letrec, types, datatypes, and patterns. Programming in a functional language: recursion patterns, useful combinators, maps, and folds, for datatypes. Example applications: recursive descent parsing, unification, combinatorial algorithms, theorem proving.', null, 3.0, '<course cid="3626">Computer Science 313</course> or <course cid="44677">351</course>; and Computer Science 349 or <course cid="3640">449</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (32229, 'CPSC', 522, 'Introduction to Randomized Algorithms', 'Techniques for the design and analysis of randomized algorithms; discrete probability theory; randomized data structures; lower bound techniques; randomized complexity classes; advanced algorithmic applications from various areas.', null, 3.0, '<course cid="3635">Computer Science 413</course>.', 'Credit for <course cid="32229">Computer Science 522</course> and <course cid="32230">622</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (30261, 'CPSC', 525, 'Principles of Computer Security', 'Security policies and protection mechanisms for a computing system, including such topics as design principles of protection systems, authentication and authorization, reference monitors, security architecture of popular platforms, formal modelling of protection systems, discretionary access control, safety analysis, information flow control, integrity, role-based access control. Legal and ethical considerations will be introduced.', null, 3.0, '<course cid="3642">Computer Science 457</course>; and 3 units from <course cid="44677">Computer Science 351</course>, <course cid="6947">Mathematics 271</course> or <course cid="6948">273</course>.', 'Credit for <course cid="30261">Computer Science 525</course> and either 529 or <course cid="30274">625</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (30270, 'CPSC', 526, 'Network Systems Security', 'Attacks on networked systems, tools and techniques for detection and protection against attacks including firewalls and intrusion detection and protection systems, authentication and identification in distributed systems, cryptographic protocols for IP networks, security protocols for emerging networks and technologies, privacy enhancing communication. Legal and ethical issues will be introduced.', null, 3.0, '<course cid="30259">Computer Science 329</course> and <course cid="3639">441</course>.', 'Credit for <course cid="30270">Computer Science 526</course> and either 529 or <course cid="30275">626</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3658, 'CPSC', 527, 'Computer Viruses and Malware', 'Study of computer viruses, worms, Trojan horses, and other forms of malicious software. Countermeasures to malicious software. Legal and ethical issues, and some general computer and network security issues.', null, 3.0, '<course cid="3626">Computer Science 313</course> and <course cid="3642">457</course> and consent of the Department.', 'Credit for <course cid="3658">Computer Science 527</course> and 627 will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3659, 'CPSC', 528, 'Spam and Spyware', 'Spam and other unsolicited bulk electronic communication, and spyware. Legal and ethical issues. Countermeasures and related security problems.', null, 3.0, '<course cid="3626">Computer Science 313</course> and <course cid="3642">457</course> and consent of the Department.', 'Credit for <course cid="3659">Computer Science 528</course> and 628 will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (30271, 'CPSC', 530, 'Information Theory and Security', 'Information theoretic concepts such as entropy and mutual information and their applications to defining and evaluating information security systems including encryption, authentication, secret sharing and secure message transmission.', null, 3.0, '<course cid="44677">Computer Science 351</course>; or 3 units from <course cid="3620">Computer Science 219</course>, <course cid="3622">233</course> or <course cid="3623">235</course> and 3 units from <course cid="6947">Mathematics 271</course>, <course cid="6948">273</course>, <course cid="43123">315</course> and 3 units from <course cid="39462">Statistics 205</course>, <course cid="6337">213</course>, <course cid="40118">321</course>.', 'Credit for <course cid="30271">Computer Science 530</course> and <course cid="30276">630</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3661, 'CPSC', 531, 'Systems Modelling and Simulation', 'An introduction to the modelling and simulation of stochastic systems; programming language issues; model and tool design; input data modelling; simulation experiments; and the interpretation of simulation results.', null, 3.0, '<course cid="3642">Computer Science 457</course> and 3 units from Mathematics 321, <course cid="39462">Statistics 205</course>, 211, <course cid="6337">213</course> or <course cid="40118">321</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3662, 'CPSC', 535, 'Introduction to Image Analysis and Computer Vision', 'Standard methods used in the analysis of digital images. Image acquisition and display: visual perception; digital representation. Sampling and enhancement. Feature extraction and classification methods. Object recognition.', null, 3.0, '3 units from <course cid="6951">Mathematics 311</course>, <course cid="6952">313</course>, <course cid="6955">331</course>, 353, <course cid="43131">376</course>, Applied Mathematics 307, 311, Pure Mathematics 331.', 'Credit for <course cid="3662">Computer Science 535</course> and <course cid="3690">635</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (47032, 'CPSC', 544, 'Machine Learning', 'Presenting various techniques for learning various kinds of knowledge, including rules, parameters, trees and graphs, partitions of sets, sequences and behaviors, and cases. Pointing out the components that can be improved using knowledge about the application area of a system. Looking at various pre-processing and post-processing improvement techniques.', null, 3.0, '<course cid="3638">Computer Science 433</course>.', 'Credit for <course cid="47032">Computer Science 544</course> and <course cid="3677">599</course>.44 will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3663, 'CPSC', 550, 'Systems Administration', 'Topics and practices in systems administration and management. Required and optional administration duties and responsibilities. Moral and ethical conundrums, and legal responsibilities, in systems operation. Configuration and installation of operating systems and network and systems services.', null, 6.0, '<course cid="3639">Computer Science 441</course>, <course cid="3642">457</course> and consent of the Department.', null, null, false, false, null);
INSERT INTO `course` VALUES (3664, 'CPSC', 559, 'Introduction to Distributed Systems', 'Designing and implementing distributed systems that overcome challenges due to concurrent computation, failure of components in the system and heterogeneity of processors and communication channels.', null, 3.0, '<course cid="3639">Computer Science 441</course> and <course cid="3642">457</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3665, 'CPSC', 561, 'Introduction to Distributed Algorithms', 'Basic problems in distributed systems such as symmetry breaking, consensus, resource allocation, and synchronization. The impact of system characteristics, such as models of communication, timing and failure, and of solution requirements, such as correctness and complexity criteria and algorithmic constraints, on the computability and complexity of these problems. Techniques for solving problems under different models will be emphasized.', null, 3.0, '<course cid="3635">Computer Science 413</course>.', 'Credit for <course cid="3665">Computer Science 561</course> and <course cid="3694">661</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3666, 'CPSC', 565, 'Emergent Computing', 'An insight into a new mindset for programming as an emergent and evolutionary process of "breeding," rather than constructing. Programs can evolve to perform specific tasks in a bottom-up fashion rather than being manually coded. Topics will include: decentralized agent-based programming, massive parallelism and interaction, evolution, swarm intelligence.', null, 3.0, '3 units from <course cid="3619">Computer Science 217</course>, <course cid="3621">231</course>, <course cid="43036">Data Science 211</course>; and 6 units offered by the Faculty of Science at the 400 level or above.', null, null, false, false, null);
INSERT INTO `course` VALUES (3667, 'CPSC', 567, 'Foundations of Multi-Agent Systems', 'Modelling of agents and properties of multi-agent systems. Communication issues, including interaction and co-ordination concepts, forming and maintaining organizations, and competitive agent environments. Example systems; the implementation of a multi-agent system will be performed as the assignment.', null, 3.0, '<course cid="3642">Computer Science 457</course> and <course cid="3638">433</course>.', 'Credit for <course cid="3667">Computer Science 567</course> and <course cid="3681">609</course> or <course cid="10279">Software Engineering 697</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (30272, 'CPSC', 568, 'Agent Communications', 'An examination of communication paradigms in multi-agent systems. A number of paradigms will be covered including simple protocols, BDI (Believe, Desire, Intension), and social commitments.', null, 3.0, '<course cid="3638">Computer Science 433</course> and consent of the Department.', 'Credit for <course cid="30272">Computer Science 568</course> and <course cid="30277">662</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3668, 'CPSC', 571, 'Design and Implementation of Database Systems', 'Implementation and design of modern database systems including query modification/optimization, recovery, concurrency, integrity, and distribution.', null, 3.0, '<course cid="3644">Computer Science 471</course>.', 'Credit for <course cid="3668">Computer Science 571</course> and <course cid="3697">671</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (39454, 'CPSC', 572, 'Fundamentals of Social Network Analysis and Data Mining', 'Introduction to data mining with emphasis on frequent pattern mining, clustering and classification, data collection, network construction, basic graph theory concepts and network analysis metrics, and case studies.', null, 3.0, '<course cid="3644">Computer Science 471</course>.', 'Credit for <course cid="39454">Computer Science 572</course> and either <course cid="3677">599</course>.77 and <course cid="39455">672</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (43030, 'CPSC', 575, 'iProgramming for Creative Minds', 'Design, develop and market apps. This course has a focus on developing, implementing and evaluating ideas in teams and gaining entrepreneurial skills for software development and marketing.', null, 3.0, '3 units from <course cid="42224">Software Engineering 300</course>, 301, <course cid="43039">Data Science 311</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (43031, 'CPSC', 577, 'Biometric Technologies', 'Principles of biometric system design, technology and performance evaluation. Verification, identification and synthesis in biometrics. Traditional and emerging techniques for fingerprint matching, face recognition, iris modelling, signature authentication, and biometric pattern recognition. Multi-modal biometrics and information fusion. Privacy and ethics considerations in deployment of biometric systems.', null, 3.0, '<course cid="3641">Computer Science 453</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3669, 'CPSC', 581, 'Human-Computer Interaction II', 'Intermediate and advanced topics and applications in human-computer interaction, to further one's skills for designing highly interactive human-computer interfaces.', null, 3.0, '<course cid="3645">Computer Science 481</course> and consent of the Department.', null, null, false, false, null);
INSERT INTO `course` VALUES (3670, 'CPSC', 583, 'Introduction to Information Visualization', 'Principles of information representation, presentation and interaction. Development of mappings from data to visual structures and exploration, navigation, cues, distortion and emphasis techniques.', null, 3.0, '3 units from <course cid="30258">Computer Science 319</course>, <course cid="3628">331</course> or <course cid="43039">Data Science 311</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (30273, 'CPSC', 584, 'Human-Robot Interaction', 'Introduction to the design, implementation and evaluation of human-robot interfaces. Topics include the evaluation of human-robot interaction (HRI), theoretical, philosophical and ethical issues, exploration of applications and tasks, prototyping HRI tools, and practical implementation and evaluation methods.', null, 3.0, '<course cid="3645">Computer Science 481</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3671, 'CPSC', 585, 'Games Programming', 'Standard techniques for the implementation of computer games. Standard multimedia programming environments and high performance multimedia. Special purpose rendering engines. Interactive control and feedback; modelling.', null, 3.0, '<course cid="3641">Computer Science 453</course> and consent of the Department.', null, null, false, false, null);
INSERT INTO `course` VALUES (3672, 'CPSC', 587, 'Fundamentals of Computer Animation', 'Principles of traditional animation, key framing, parametric and track animation, free form deformation, inverse kinematics, dynamics, spring mass systems, particle systems, numerical integration, Lagrangian constraints, space time constraints, collisions, human animation, behavioural animation, metamorphosis, implicit animation techniques, animating liquids, gases and cloth, motion capture.', null, 3.0, '<course cid="3641">Computer Science 453</course>.', 'Credit for <course cid="3672">Computer Science 587</course> and <course cid="3702">687</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3673, 'CPSC', 589, 'Modelling for Computer Graphics', 'Parametric Modelling. B-splines and NURBS. Subdivision schemes. Surface subdivision. Multiresolution. Wavelets. Implicit modelling. Blends. Polygonization. Blobtree. Precise contact modelling. Solid modelling. CSG. Procedural modelling. Special topics, e.g. Differential geometry. Graph-based modelling. Topology.', null, 3.0, '<course cid="3641">Computer Science 453</course>.', 'Credit for <course cid="3673">Computer Science 589</course> and <course cid="3703">689</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3674, 'CPSC', 591, 'Rendering', 'Physical foundations of illuminations techniques. Colour. Radiometry and photometry. Reflection models. The rendering equation. Ray tracing. Monte Carlo techniques. Sampling and antialiasing. Texturing. Radiosity. Photon tracing. Volume rendering. Image-based rendering. Real-time shading.', null, 3.0, '<course cid="3641">Computer Science 453</course>.', 'Credit for <course cid="3674">Computer Science 591</course> and <course cid="3704">691</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3675, 'CPSC', 594, 'Software Engineering Project', 'A software engineering project conducted under the guidance of a faculty member.', null, 6.0, 'Consent of the Department.', null, null, false, false, 'Includes a research, writing and presentation component.');
INSERT INTO `course` VALUES (3676, 'CPSC', 598, 'Special Topics in Computer Science', 'New areas in Computer Science. It will be offered only as required. Before registration consult the Department of Computer Science for topics offered.', null, 6.0, 'Consent of the Department.', null, null, false, true, null);
INSERT INTO `course` VALUES (3677, 'CPSC', 599, 'Special Topics in Computer Science', 'Exploration of various areas in Computer Science. Topics will vary from year-to-year. It will be offered as required to provide the opportunity for students to engage in additional areas in Computer Science. Before registration, consult the Department of Computer Science for topics offered.', null, 3.0, 'Consent of the Department.', null, null, false, true, null);
INSERT INTO `course` VALUES (3678, 'CPSC', 601, 'Special Topics in Computer Science', 'A study of problems of particular interest to graduate students in Computer Science.', null, 3.0, null, null, null, false, true, null);
INSERT INTO `course` VALUES (47284, 'CPSC', 602, 'Explorations in Information Security and Privacy', 'Surveys topics in information security and privacy, with the purposes of cultivating an appropriate mindset for approaching security and privacy issues and developing basic familiarity with related technical controls.', null, 3.0, null, null, null, false, false, 'Not intended for students specializing in information security and privacy technologies.');
INSERT INTO `course` VALUES (47285, 'CPSC', 604, 'Foundations of Access Control', 'Application of formal methods and mathematical logic to the modelling of access control systems and the design of policy languages. Topics include safety and resiliency analysis, proof of policy compliance, expressiveness of policy languages, policy analysis, as well as a survey of modern paradigms of access control.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3679, 'CPSC', 605, 'Information Storage and Processing in Biological Systems', 'Examination of complex biological systems; concepts and fundamentals of biological solutions to information storage and processing; modelling and computer simulation of biological systems; information storage in biological molecules; genetic networks; hierarchical organization of biological information processing in signal transduction, development, evolution, and ecology; biological control systems.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3680, 'CPSC', 607, 'Biological Computation', 'Examination and modelling of biological networks; focus on the latest developments in biological computing and their theoretical backgrounds, such as: DNA computing; genomic algorithms; artificial chemistries; complex adaptive systems, chaos and fractals; immune system computing; gene regulatory networks; swarm intelligence systems.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3681, 'CPSC', 609, 'Foundations of Multi-Agent Systems', 'Modelling of agents and properties of multi-agent systems. Communication issues, including interaction and co-ordination concepts, forming and maintaining organizations, and competitive agent environments. Example systems; the implementation of a multi-agent system will be performed as the assignment.', null, 3.0, null, 'Credit for <course cid="3681">Computer Science 609</course> and either <course cid="3667">Computer Science 567</course> or <course cid="10279">Software Engineering 697</course> will not be allowed for programs offered by the Department of Computer Science.', null, false, false, null);
INSERT INTO `course` VALUES (3682, 'CPSC', 610, 'Compiler Code Generation and Optimization', 'Compiler code generation and optimization techniques, including register allocation, instruction selection, dataflow analysis, and code optimization techniques using intermediate representations. Implementation of special language features and tools for automated code generation.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3683, 'CPSC', 611, 'Complexity Theory', 'Deterministic and non-deterministic time and space complexity; complexity classes and hierarchies; NP-complete problems and intractable problems; axiomatic complexity theory.', null, 3.0, null, 'Credit for <course cid="3683">Computer Science 611</course> and <course cid="3651">Computer Science 511</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (40997, 'CPSC', 615, 'Computational Techniques for Graphics and Visualization', 'Various case studies from the fields of graphics and visualization. Topics in numerical linear algebra, numerical optimization, and discrete differential geometry.', null, 3.0, null, 'Credit for <course cid="40997">Computer Science 615</course> and <course cid="3678">601</course>.13 will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3685, 'CPSC', 617, 'Category Theory for Computer Science', 'Introduction to category theory with applications in computer science. Functors, natural transformations, adjoints and monads, initial and final algebras. Introduction to 2-categories and fibrations.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3686, 'CPSC', 619, 'Quantum Computation', 'Introduction to quantum computing. Quantum algorithms, quantum search, quantum fourier transforms, quantum error correcting codes, quantum cryptography, nonlocality and quantum communication complexity, and quantum computational complexity.', null, 3.0, null, 'Credit for <course cid="3686">Computer Science 619</course> and <course cid="3655">519</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (32230, 'CPSC', 622, 'Randomized Algorithms', 'Design and analysis of randomized algorithms; discrete probability theory; randomized data structures; lower bound techniques; randomized complexity classes; advanced algorithmic applications from various areas.', null, 3.0, null, 'Credit for <course cid="32230">Computer Science 622</course> and <course cid="32229">522</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (30274, 'CPSC', 625, 'Principles of Computer Security', 'Security policies and protection mechanisms for a computing system, including such topics as design principles of protection systems, authentication and authorization, reference monitors, security architecture of popular platforms, formal modelling of protection systems, discretionary access control, safety analysis, information flow control, integrity, role-based access control. Legal and ethical considerations will be introduced as necessary.', null, 3.0, null, 'Credit for <course cid="30274">Computer Science 625</course> and <course cid="30261">525</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (30275, 'CPSC', 626, 'Network Systems Security', 'Attacks on networked systems, tools and techniques for detection and protection against attacks including firewalls and intrusion detection and protection systems, authentication and identification in distributed systems, cryptographic protocols for IP networks, security protocols for emerging networks and technologies, privacy enhancing communication. Legal and ethical issues will be introduced as necessary.', null, 3.0, null, 'Credit for <course cid="30275">Computer Science 626</course> and <course cid="30270">526</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3689, 'CPSC', 629, 'Elliptic Curves and Cryptography', 'An introduction to elliptic curves over the rationals and finite fields. The focus is on both theoretical and computational aspects; subjects covered will include the study of endomorphism rings, Weil pairing, torsion points, group structure, and effective implementation of point addition. Applications to cryptography will be discussed, including elliptic curve-based Diffie-Helman key exchange, El Gamal encryption, and digital signatures, as well as the associated computational problems on which their security is based.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (30276, 'CPSC', 630, 'Information Theory and Security', 'Information theoretic concepts such as entropy and mutual information, and their applications to defining and evaluating information security systems including encryption, authentication, secret sharing and secure message transmission.', null, 3.0, null, 'Credit for <course cid="30276">Computer Science 630</course> and <course cid="30271">530</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3690, 'CPSC', 635, 'Image Analysis and Computer Vision', 'Standard methods used in the analysis of digital images. Image acquisition and display: visual perception; digital representation. Sampling and enhancement. Feature extraction and classification methods. Object recognition.', null, 3.0, null, 'Credit for <course cid="3690">Computer Science 635</course> and <course cid="3662">535</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3691, 'CPSC', 641, 'Performance Issues in High Speed Networks', 'An overview of current research in high speed networks. Topics covered will include the current Internet, the future Internet, wireless networks, optical networks, Asynchronous Transfer Mode (ATM), TCP/IP, network traffic measurement, web server performance, and mobile computing. Emphasis will be placed on network performance issues for next-generation Internet protocols and applications.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (32231, 'CPSC', 643, 'Modern Wireless Networks', 'An introduction to the fundamentals and applications of wireless networks.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3692, 'CPSC', 653, 'Computational Geometry', 'Geometric searching, hull proximity and intersection data structures and algorithms and their complexity.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3693, 'CPSC', 657, 'Modelling And Visualization of Plants', 'Modelling, simulation and visualization of plants for computer graphics and biological purposes. Modelling of plants as an example of interdisciplinary research including computer science, biology, mathematics and physics. L-systems as a formal basis for model construction. Modelling languages. Information flow in plants. Symmetry, self-similarity and allometry of plants. Descriptive models of plant architecture. Models integrating plant structure and function. Simulation of plant development. Case studies: competition for space, phyllotaxis, tropisms, and biomechanical considerations. Reaction-diffusion models of morphogensis. Genotype-to-phenotype mapping. Modelling of plant ecosystems. Rendering and visualization of the models. A survey of applications and research directions.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3694, 'CPSC', 661, 'Algorithms for Distributed Computation', 'Fundamental algorithmic problems in distributed computation; impact of communication, timing, failures and other characteristics on computability and complexity of solutions.', null, 3.0, null, 'Credit for <course cid="3694">Computer Science 661</course> and <course cid="3665">561</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (30277, 'CPSC', 662, 'Agent Communications', 'An examination of communication paradigms in multi-agent systems. A number of paradigms will be covered including simple protocols, BDI (Believe, Desire, Intension), and social commitments.', null, 3.0, null, 'Credit for <course cid="30277">Computer Science 662</course> and <course cid="30272">568</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3695, 'CPSC', 667, 'Computer Algebra', 'Fundamental problems, classical and modern algorithms, and algorithm design and analysis techniques of use in computer algebra. Integer and polynomial arithmetic. Additional problems in computer algebra, possibly including problems in computational linear algebra, factorization, and concerning systems of polynomial equations will be considered as time permits.', null, 3.0, null, 'Credit for <course cid="3695">Computer Science 667</course> and <course cid="3654">518</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3696, 'CPSC', 669, 'Cryptography', 'An overview of the basic techniques in modern cryptography, with emphasis on fit-for-application primitives and protocols. Topics will include symmetric and public-key cryptosystems; digital signatures; elliptic curve cryptography; key management; attack models and well-defined notions of security.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3697, 'CPSC', 671, 'Database Management Systems', 'Foundations of database applications and database systems, plus some advanced topics in data management systems will be introduced.', null, 3.0, null, 'Credit for <course cid="3697">Computer Science 671</course> and <course cid="3668">571</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (39455, 'CPSC', 672, 'Fundamentals of Social Network Analysis and Data Mining', 'Introduction to data mining with emphasis on frequent pattern mining, clustering and classification, data collection, network construction, basic graph theory concepts and network analysis metrics, and case studies.', null, 3.0, null, 'Credit for <course cid="39455">Computer Science 672</course> and either <course cid="39454">572</course> or <course cid="3677">599</course>.77 will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3698, 'CPSC', 673, 'Distributed Database Systems', 'Introduction to distributed database systems. Topics covered include: architecture, data design, query processing, transaction management, multidatabases, object-oriented databases and advanced system issues.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3699, 'CPSC', 675, 'Datawarehouse Systems', 'Design, development and deployment of datawarehouses. Schemas, models, data organization, OLAP, tuning, data mining and architectural models may be discussed.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3700, 'CPSC', 681, 'Research Methods in Human-Computer Interaction', 'Application of the theory and methodology of human-machine studies to real systems; theory and practice.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3701, 'CPSC', 683, 'Information Visualization: Theory and Practice', 'The theory and development of interactive visual representations of abstract data for the purpose of amplifying cognition. Topics covered can include representational issues, perceptual issues, visual literacy, spatial abstraction, and interaction issues.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3702, 'CPSC', 687, 'Computer Animation', 'Principles of traditional animation, key framing, parametric and track animation, free form deformation, inverse kinematics, dynamics, spring mass systems, particle systems, numerical integration, Lagrangian constraints, space time constraints, collisions, human animation, behavioural animation, metamorphosis, implicit animation techniques, animating liquids, gases and cloth, motion capture.', null, 3.0, null, 'Credit for <course cid="3702">Computer Science 687</course> and <course cid="3672">587</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3703, 'CPSC', 689, 'Modelling for Computer Graphics', 'Parametric Modelling. B-splines and NURBS. Subdivision schemes. Surface subdivision. Multiresolution. Wavelets. Implicit modelling. Blends. Polygonization. Blobtree. Precise contact modelling. Solid modelling. CSG. Procedural modelling. Special topics, e.g. Differential geometry. Graph-based modelling. Topology.', null, 3.0, null, 'Credit for <course cid="3703">Computer Science 689</course> and <course cid="3673">589</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3704, 'CPSC', 691, 'Rendering', 'Physical foundations of illuminations techniques. Colour. Radiometry and photometry. Reflection models. The rendering equation. Ray tracing. Monte Carlo techniques. Sampling and antialiasing. Texturing. Radiosity. Photon tracing. Volume rendering. Image-based rendering. Real-time shading.', null, 3.0, null, 'Credit for <course cid="3704">Computer Science 691</course> and <course cid="3674">591</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3705, 'CPSC', 695, 'Data Management in Geographical Information Systems', 'Examination of advanced geometric algorithms for representation, analysis and visualization of Geographical Information Systems. Data structures such as progressive mesh, ROAM, multidimensional Delauney triangulization, quadtree and space partitioning. Algorithmic techniques such as incremental, divide and conquer, sweep-plane, and dimension reduction. Algorithms for surface simplification, culling, quality measurement and reduction.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (32232, 'CPSC', 696, 'Information Security Seminar', 'Topics in information security, such as security management, emerging threats, research frontiers using case studies and best practices.', null, 3.0, null, 'Credit for <course cid="32232">Computer Science 696</course> and <course cid="3707">699</course> will not be allowed.', null, false, false, null);
INSERT INTO `course` VALUES (3706, 'CPSC', 697, 'Biometric Security', 'Principles of biometric system design, technology and performance evaluation. Verification, identification and synthesis in biometrics. Traditional and emerging techniques for fingerprint matching, face recognition, iris modelling, signature authentication, and biometric pattern recognition. Multi-modal biometrics and biometric security.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (32233, 'CPSC', 698, 'Information Security Project', 'An information security project conducted under the guidance of a faculty member. A report must be written and presented on completion of the course.', null, 6.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3707, 'CPSC', 699, 'Research Methodology in Computer Science', 'An introduction to and survey of research areas and methods in Computer Science. Professional skills in computer science research such as reviewing, critical evaluation, and the preparation of research proposals.', null, 3.0, null, 'Credit for <course cid="3707">Computer Science 699</course> and <course cid="32232">696</course> will not be allowed.', null, true, false, null);
INSERT INTO `course` VALUES (3708, 'CPSC', 701, 'Research Topics in Computer Science', 'In-depth course on a focused current research topic in Computer Science. Involves a significant research component and requires substantial background knowledge.', null, 3.0, null, null, null, false, true, null);
INSERT INTO `course` VALUES (3709, 'CPSC', 767, 'Advanced Topics in Multiagent Systems', 'An in-depth study of a selected subfield of multiagent systems including state-of-the-art research. This is a project-driven course.', null, 3.0, '<course cid="3667">Computer Science 567</course> or <course cid="3681">609</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3710, 'CPSC', 771, 'Current Trends in Database Technology', 'Advanced topics chosen from Bioinformatics, Data mining, Mobile Databases, Spatial Databases and Web Databases. There is a large project component.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3711, 'CPSC', 781, 'Advanced Topics in Human-Computer Interaction', 'The topics covered will change year-by-year depending on current advances in human computer interaction.', null, 3.0, '<course cid="3645">Computer Science 481</course>.', null, null, false, false, null);
INSERT INTO `course` VALUES (3712, 'CPSC', 785, 'Implicit Modelling', 'A detailed look at modelling using implicit and iso-surface techniques taking an in-depth review of the literature. Algebraic methods will be followed by skeletal models, field function design, modelling techniques, rendering and texture mapping. Polygonisation algorithms, ray tracing implicits, techniques for animation, meta-morphosis, precise contact modelling, deformation and warping. Algorithms and data structures and implementation details will be presented. Students will be expected to make a new contribution in their project and term paper.', null, 3.0, null, null, null, false, false, null);
INSERT INTO `course` VALUES (3713, 'CPSC', 789, 'Advanced Geometric Modelling', 'Current research topics including spline modelling, Subdivision Surfaces, multiresolution, wavelets, analysis of the subdivision surfaces and reverse subdivision.', null, 3.0, null, null, null, false, false, null);
